import React, {useState, useEffect} from "react";
import { useParams } from "react-router-dom";
import "./StockDetails.css";
import { Tab, Tabs } from '@mui/material';

function StockDetailsComponent() {
  const { id } = useParams();
  const [data, setData] = useState(null);

  const [value, setValue] = useState(0); // Define the initial value for the tab


  const handleChange = (newValue) => {
    setValue(newValue);
  };

  useEffect(() => {
    // Perform API call here and set the data
    // http://localhost:8080/ticker/NVDA
    fetch(`http://localhost:8080/ticker/${id}`)
      .then(response => response.json())
      .then(data => {
        setData(data);
      });
  }, [id]);

  return (
    <div className="container resultPage">
      <div className="row">
        <div className="col leftCol">
          <div className="container companyData">
            <div className="row">
              <div className="col">
                <h1>
                  {/* {id} */}
                  Ticker ID
                </h1>
              </div>
              <div className="col">
                <h5>
                  {/* {id} */}
                  fav_btn
                </h5>
              </div>
            </div>

            <div className="row">
              <h3>
                {/* {id} */}
                Company Name
              </h3>
            </div>

            <div className="row">
              <h5>
                {/* {id} */}
                Stock Index
              </h5>
            </div>
          </div>
        </div>

        <div className="col rightCol">
          <div className="container stockValues">
            <div className="row justify-content-end">
              <h1>
                {/* {id} */}
                Stock Value
              </h1>
            </div>
            <div className="row justify-content-end">
              <h3>
                {/* {id} */}
                Stock Points
              </h3>
            </div>
            <div className="row justify-content-end">
              <h6>
                {/* {id} */}
                Time Stamp
              </h6>
            </div>
          </div>
        </div>
      </div>

      <div className="row"
        style={{marginTop:"30px"}}
      >
        <Tabs value={value} onChange={handleChange} centered variant="fullWidth">
            <Tab label="Summary" />
            <Tab label="Top News" />
            <Tab label="Charts" />
        </Tabs>
      </div>
    </div>
  );
}

export default StockDetailsComponent;
