                  <h5>
                    {/* {id} */}
                    fav_btn
                  </h5>
                </div>
              </div>

              <div className="row">
                <h3>
                  name
                  {/* {tickerData['name']} */}
                </h3>
              </div>

              <div className="row">
                <h5>
                  exchangecode
                  {/* {tickerData['exchangeCode']} */}
                </h5>
              </div>
            </div>
          </div>

          <div className="col rightCol">
            <div className="container stockValues">
              <div className="row justify-content-end">
                <h1>
                  {/* {id} */}
                  Stock Value
                </h1>
              </div>
              <div className="row justify-content-end">
                <h3>
                  {/* {id} */}
                  Stock Points
                </h3>
              </div>
              <div className="row justify-content-end">
                <h6>
                  {/* {id} */}
                  get current time
                </h6>
              </div>
            </div>
          </div>
        </div>

        {/* <div className="row" style={{ marginTop: "30px" }}>

            <Tabs
              value={value}
              onChange={handleChange}
              centered
              variant="fullWidth"
            >
              <Tab label="Summary" />
              <Tab label="Top News" />
              <Tab label="Charts" />
            </Tabs>

            <TabPanel value={value} index={0}>
              Item One
            </TabPanel>
        </div> */}

        {/* <div className="row" style={{ marginTop: "30px" }}>
          <Box sx={{ borderBottom: 1, borderColor: "divider" }}>
            <Tabs
              value={value}
