import React, { useState, useEffect } from "react";
import { Card, ListGroup, Button } from "react-bootstrap";

function PortfolioComponent() {
  const [noStocks, setNoStocks] = useState(true);
  const [portfolioStocks, setPortfolioStocks] = useState([]);

  useEffect(() => {
    const existingPortfolio = localStorage.getItem("portfolio");
    console.log("Existing Portfolio: " + existingPortfolio);

    if (existingPortfolio) {
      const portfolio = JSON.parse(existingPortfolio);

      if (portfolio.length === 0) {
        setNoStocks(true);
      } else {
        setNoStocks(false);

        portfolio.forEach((stock) => {
              //   fetch(`http://localhost:8080/ticker/${stock.symbol}`)
              //     .then((response) => response.json())
              //     .then((data) => {
              //       console.log("Data: ", data);
              //       const updatedStock = { ...stock, data: data };
              //       return updatedStock;
              //     })

              fetch(`http://localhost:8080/iex/${stock.symbol}`)
                .then((response) => response.json())
                .then((data) => {
                  console.log("IEX Data: ");
                  console.log(data);
                  stock.data = data[0];
                  console.log("Stock Data: ");
                  console.log(stock.data);
                })
        });
            // console.log("Stock: ", stock);
        //   )
        //   .then((updatedPortfolio) => {
        //     console.log("Updated Portfolio: ");
        //     console.log(updatedPortfolio);
        //     setPortfolioStocks(updatedPortfolio);
        //   })
        //   .catch((error) => {
        //     console.log("Error fetching portfolio stocks:", error);
        //   });
      }
    }
  }, []);

  return (
    <div>
      <h2>Portfolio</h2>

      {noStocks && (
        <div>
          <Card
            style={{
              marginRight: "20px",
              marginLeft: "20px",
              marginTop: "10px",
              maxWidth: "70%",
            }}
          >
            <Card.Body>No stocks in watchlist</Card.Body>
          </Card>
        </div>
      )}

      {!noStocks && (
        <ListGroup>
          {/* {favoriteStocks.map((stock) => ( */}
          <ListGroup.Item
            //   key={stock.id}
            style={{ border: "none", alignItems: "center" }}
          >
            <Card
              style={{
                marginRight: "20px",
                marginLeft: "20px",
                marginTop: "10px",
                maxWidth: "70%",
              }}
            >
              <Card.Body>
                {/* bootstrap data into 3 columns */}
                <div className="container">
                  <div className="row">
                    <div className="col d-flex flex-column align-items-center">
                      {/* <h1>{stock.symbol}</h1> */}
                      <h2>Stock Name</h2>
                    </div>
                    <div className="col d-flex flex-column align-items-center">
                      <h1>Ticker Score</h1>
                      <h2>Up and Down Score</h2>
                    </div>
                    <div className="col ">
                      <div className="row d-flex flex-column align-items-center">
                        <Button
                          variant="primary"
                          // onClick={() => handleCardClick(stock.symbol)}
                          style={{ marginBottom: "10px" }}
                        >
                          Open
                        </Button>
                      </div>
                      <div className="row d-flex flex-column align-items-center">
                        <Button
                          variant="danger"
                          // onClick={() => handleDelete(stock.symbol)}
                        >
                          Delete
                        </Button>
                      </div>
                    </div>
                  </div>
                </div>
              </Card.Body>
            </Card>
          </ListGroup.Item>
          ){/* ) */}
          {/* } */}
        </ListGroup>
      )}
    </div>
  );
}

export default PortfolioComponent;
