import React, { useEffect, useState } from "react";
import { Card, ListGroup, Button } from 'react-bootstrap';
import { useHistory } from "react-router-dom";

function WatchlistComponent() {
  const [favoriteStocks, setFavoriteStocks] = useState([]);
  const history = useHistory();

  useEffect(() => {
    const existingFav = localStorage.getItem("fav");
    console.log(existingFav);


    if (existingFav) {
      const fav = JSON.parse(existingFav);
      const sortedFav = fav.sort((a, b) => a.symbol.localeCompare(b.symbol));

      sortedFav.forEach((stock) => {

        // fetch(`http://localhost:8080/iex/${stock.symbol}`)
        // .then((response) => response.json())
        // .then((data) => {

        // console.log("IEX Data: ");
        // console.log(data);
        // stock.data = data[0];
        // console.log("Stock Data: ");
        // console.log(stock.data);
        // });


        // http://localhost:8080/ticker/NVDA
        // fetch(`http://localhost:8080/ticker/${id}`)
        //   .then((response) => response.json())
        //   .then((data) => {
        //     setTickerData(data);
        //     console.log("Data: ");
        //     console.log(data);
        //   });

      });


      setFavoriteStocks(sortedFav);
    //   console.log("Favorite Stocks: " + JSON.parse(sortedFav));
    }
  }, []);

  const handleDelete = (symbol) => {
    // const updatedStocks = favoriteStocks.filter((stock) => stock.symbol !== symbol);
    // setFavoriteStocks(updatedStocks);

    const existingFav = localStorage.getItem("fav");
  if (existingFav) {
    const fav = JSON.parse(existingFav);
    const updatedFav = fav.filter((stock) => stock.symbol !== symbol);
    localStorage.setItem("fav", JSON.stringify(updatedFav));
    setFavoriteStocks(updatedFav);
  }
  };


  const handleCardClick = (symbol) => {
    console.log("Card Clicked: " + symbol);

    history.push(`/details/${symbol}`);
    };

  return (
    <div>
      <h2>Watchlist</h2>
      <ListGroup>
      {favoriteStocks.map((stock) => (
        <ListGroup.Item key={stock.id} style={{border:"none"}}>
          <Card 
          style={{marginRight:"20px", marginLeft:"20px", marginTop:"10px"}}
          onClick={() => handleCardClick(stock.symbol)}>
            <Card.Body>
              <Card.Title>{stock.symbol}</Card.Title>
              <Button variant="danger" onClick={() => handleDelete(stock.symbol)}>
                Delete
              </Button>
            </Card.Body>
          </Card>
        </ListGroup.Item>
      ))}
    </ListGroup>


    </div>
  );
}

export default WatchlistComponent;
