import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

function NewsDetailsComponent({ newsData }) {

    const [selectedNews, setSelectedNews] = useState(null);



    const cardClickHandler = (news) => {
        setSelectedNews(news);
        console.log(news);
    };




    return (
    <div className="row row-cols-1 row-cols-md-2 g-4">
      {newsData.slice(0, 10).map((news, index) => (
        <div className="col-md-6" key={index}>
          <div className="card"
          onClick={()=>cardClickHandler(news)}>
            <div className="row g-0 align-items-center">
              <div className="col-md-4">
                <img src={news.urlToImage} alt="News" className="img-fluid img-thumbnail rounded-start" 
                                style={{ height: "80%", width:"80%", objectFit: "contain" }}
                                />
              </div>
              <div className="col-md-8">
                <div className="card-body">
                  <h6 className="card-title">
                    {news.title}
                    </h6>
                </div>
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default NewsDetailsComponent;
