import React, { useEffect, useState } from "react";
import { Card, ListGroup, Button } from "react-bootstrap";
import { useHistory, useNavigate } from "react-router-dom";

function WatchlistComponent() {
  const [favoriteStocks, setFavoriteStocks] = useState([]);
  const [noStocks, setNoStocks] = useState(true);

  const navigate = useNavigate();

  useEffect(() => {
    const existingFav = localStorage.getItem("fav");
    console.log(existingFav);

    if(existingFav.length > 0) {
        setNoStocks(true);
    }
    else
    {
        setNoStocks(false);
    }

    if (existingFav) {
      const fav = JSON.parse(existingFav);
      const sortedFav = fav.sort((a, b) => a.symbol.localeCompare(b.symbol));

      sortedFav.forEach((stock) => {
        // fetch(`http://localhost:8080/iex/${stock.symbol}`)
        // .then((response) => response.json())
        // .then((data) => {
        // console.log("IEX Data: ");
        // console.log(data);
        // stock.data = data[0];
        // console.log("Stock Data: ");
        // console.log(stock.data);
        // });
        // http://localhost:8080/ticker/NVDA
        // fetch(`http://localhost:8080/ticker/${id}`)
        //   .then((response) => response.json())
        //   .then((data) => {
        //     setTickerData(data);
        //     console.log("Data: ");
        //     console.log(data);
        //   });
      });

      setFavoriteStocks(sortedFav);
      //   console.log("Favorite Stocks: " + JSON.parse(sortedFav));
    }
  }, []);

  const handleDelete = (symbol) => {
    // const updatedStocks = favoriteStocks.filter((stock) => stock.symbol !== symbol);
    // setFavoriteStocks(updatedStocks);

    const existingFav = localStorage.getItem("fav");
    if (existingFav) {
      const fav = JSON.parse(existingFav);
      const updatedFav = fav.filter((stock) => stock.symbol !== symbol);
      localStorage.setItem("fav", JSON.stringify(updatedFav));
      setFavoriteStocks(updatedFav);
    }
  };

  const handleCardClick = (symbol) => {
    console.log("Card Clicked: " + symbol);
    navigate(`/details/${symbol}`);
  };

  return (
    <div>
      <h2>Watchlist</h2>
      <ListGroup>
        {favoriteStocks.map((stock) => (
          <ListGroup.Item
            key={stock.id}
            style={{ border: "none", alignItems: "center" }}
          >
            <Card
              style={{
                marginRight: "20px",
                marginLeft: "20px",
                marginTop: "10px",
                maxWidth: "70%",
              }}

            >
              <Card.Body>
                {/* bootstrap data into 3 columns */}
                <div className="container">
                  <div className="row">
                    <div className="col d-flex flex-column align-items-center">
                      <h1>{stock.symbol}</h1>
                      <h2>Stock Name</h2>
                    </div>
                    <div className="col d-flex flex-column align-items-center">
                      <h1>Ticker Score</h1>
                      <h2>Up and Down Score</h2>
                    </div>
                    <div className="col ">
                      <div className="row d-flex flex-column align-items-center">
                        <Button
                          variant="primary"
                          onClick={() => handleCardClick(stock.symbol)}
                        >
                          Open
                        </Button>
                      </div>
                      <div className="row d-flex flex-column align-items-center">
                        <Button
                          variant="danger"
                          onClick={() => handleDelete(stock.symbol)}
                        >
                          Delete
                        </Button>
                      </div>
                    </div>
                  </div>
                </div>
              </Card.Body>
            </Card>
          </ListGroup.Item>
        ))}
      </ListGroup>
    </div>
  );
}

export default WatchlistComponent;
